// API functions

function request(data) {
    return {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
    };
}

export function getAllQuestions(callback) {
    fetch("/api/getAllQuestions")
        .then((res) => res.json())
        .then((questions) => {
            console.log("got all questions");
            callback(questions);
        });
}

export function getQuestion(questionId, callback) {
    fetch(`/api/getQuestion/${questionId}`)
        .then((res) => res.json())
        .then((question) => {
            console.log("got question:", question);
            callback(question);
        });
}

export function getAllAnswered(callback) {
    fetch("/api/getAllAnswered")
        .then((res) => res.json())
        .then((answered) => {
            console.log("got all answered");
            callback(answered);
        });
}

export function getAnswered(trainee, questionId, callback) {
    fetch(`/api/getAnswered/${trainee}/${questionId}`)
        .then((res) => res.json())
        .then((question) => {
            console.log(`got question ${questionId} for trainee ${trainee}`);
            callback(question);
        });
}

export function updateAnswered(question) {
    fetch("/api/updateAnswered", request(question)).then(() =>
        console.log(
            `Updated answered for question ${question.id} and trainee ${question.trainee}`
        )
    );
}
